import React from "react";
import {
  Dimension,
  StyleSheet,
  TouchableOpacity,
  View,
  ScrollView,
  Text,
} from "react-native";


  const Option = (props) => {
  const list = [
    { label: "Male", value: "male" },
    { label: "Female", value: "female" },
    { label: "Others", value: "others" },
  ];
  const onPressItem = (option) => {
    props.changeVisbility(false);
    props.setData(option);
  };
  const option = list.map((item, index) => {
    return (
      <TouchableOpacity
        key={index}
        style={styles.option}
        onPress={() => onPressItem(item)}
      >
        <Text style={style.text}>{item}</Text>
      </TouchableOpacity>
    );
  });
  const HEIGHT = Dimension.get("window").height;
  const WIDTH = Dimension.get("window").width;

  return (
    <TouchableOpacity
      onPress={() => props.changeVisbility}
      style={styles.container}
    >
      <View style={[styles.modal, { width: 70, height:70}]}>
        <ScrollView>{option}</ScrollView>
      </View>
    </TouchableOpacity>
  );
};
const styles = StyleSheet.create({
  container: {
    flex: 1,
   
    justifyContent: "center",
  },
  modal: {
    backgroundColor: "white",
    borderRadius: 10,
  },
  option: {
    alignItems: "flex-start",
  },
  text: {
    margin: 20,
    fontWeight: "bold",
    fontSize: 20,
  },
});
export default Option;
