import React,{useState} from 'react'

import { Alert, Modal, StyleSheet, Text, TouchableOpacity, View } from "react-native";
import Option from './Option';

 const MenuList = () => {
  const [showDropDown, setShowDropDown] = useState(true);
  const [chooseData, setchooseData] = useState('Country');
const changeVisbility =(bool)=>{
  setShowDropDown(bool)
} 

const setData =(option)=>{
  setchooseData(option)
} 
  return (
   
      <View style={styles.containerStyle}>
      <TouchableOpacity  style={[styles.button, styles.buttonOpen]}>
        <Text style={styles.text}>
        {chooseData}
        </Text>
      </TouchableOpacity>
        <Modal
        transpareny={true}
        animationType="fade"
          visible={showDropDown}
          onRequestClose={() => changeVisbility(false)}
             
        >
        <Option style={styles.modalView}
        changeVisbility={changeVisbility}
          setData={setData}
        />
        </Modal>
      
    </View>
  );
};

const styles = StyleSheet.create({
  centeredView: {
   
    justifyContent: "center",
    backgroundColor:"red",
    alignItems: "center",
    marginTop: 22
  },
  modalView: {
    margin: 20,
    backgroundColor: "white",
    borderRadius: 20,
    padding: 35,
    alignItems: "center",
    shadowColor: "#000",
    shadowOffset: {
      width: 0,
      height: 2
    },
    shadowOpacity: 0.25,
    shadowRadius: 4,
    elevation: 5
  },
  button: {
    borderRadius: 20,
    padding: 10,
    elevation: 2
  },
  buttonOpen: {
    backgroundColor: "#F194FF",
  },
  buttonClose: {
    backgroundColor: "#2196F3",
  },
  textStyle: {
    color: "white",
    fontWeight: "bold",
    textAlign: "center"
  },
  modalText: {
    marginBottom: 15,
    textAlign: "center"
  }
});

export default MenuList;
